$(document).ready(function () {
  $("li.productName.hide").slice(0, 10).removeClass("hide");
  if ($("li.productName.hide").length != 0) {
    $(".loadMore").removeClass("hide");
    $(".loadAll").removeClass("hide");
  }
  // Load More
  $(".loadMore").on("click", function (e) {
    $(this).siblings("ul");
    $(this)
      .siblings("ul")
      .children("li.productName.hide")
      .slice(0, 10)
      .removeClass("hide");
    if ($(this).siblings("ul").children("li.productName.hide").length == 0) {
      $(this).addClass("hide");
      $(this).siblings(".loadAll").addClass("hide");
      $(this).siblings(".or").addClass("hide");
    }
  });
  // Load All
  $(".loadAll").on("click", function (e) {
    $(this).siblings("ul").children("li.productName.hide").removeClass("hide");
    if ($(this).siblings("ul").children("li.productName.hide").length == 0) {
      $(this).addClass("hide");
      $(this).siblings(".loadMore").addClass("hide");
      $(this).siblings(".or").addClass("hide");
    }
  });
});
